    function testSelfTransferFrom(
        IERC20 token,
        address account,
        uint256 amount
    ) external {
        token.safeTransferFrom(account, account, amount);
        emit TransferFromCompleted(address(token), account, account, amount);
    }

    /**
     * @dev Test self-transferFromExact edge case (from == to)
