contracts/v1/tvm/interfaces/IPayTheFly.s.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

interface IPayTheFly {
    // Structs
    struct Project {
        string projectId;  // Unique identifier for the project
        string name;       // Name of the project
        address creator; // Address of the project creator
        address signer;  // Authorized signer for withdrawal verification
        bool active;      // Whether the project is active
    }

    struct ProjectBalance {
        uint256 paymentBalance;    // Balance from user payments (can be withdrawn by project owner)
        uint256 withdrawalBalance; // Balance deposited by project owner for user withdrawals
    }

    struct WithdrawalRequest {
        address user;           // Target user address (REQUIRED, must match msg.sender, cannot be address(0))
        string projectId;       // Project ID for the withdrawal
        address token;          // Token address (address(0) for ETH)
        uint256 amount;         // Amount to withdraw
        string serialNo;        // Unique withdrawal serial number (unique per project)
        uint256 deadline;       // Deadline timestamp for the withdrawal
    }

    struct PaymentRequest {
        string projectId;       // Project ID for the payment
        address token;          // Token address (address(0) for ETH)
        uint256 amount;         // Amount to pay
        string serialNo;        // Unique payment serial number
        uint256 deadline;       // Deadline timestamp for the payment
    }

    // Transaction types for unified event
    enum TxType {
        NONE,                 // 0 - Reserved
        PAYMENT,              // 1 - User payment
        WITHDRAWAL,           // 2 - User withdrawal (with signature)
        ADMIN_WITHDRAWAL,     // 3 - Project creator withdraws payment balance
        POOL_DEPOSIT,         // 4 - Project creator deposits to withdrawal pool
        POOL_WITHDRAW         // 5 - Project creator withdraws from withdrawal pool
    }

    // Events
    event ProjectCreated(
        string projectId,
        string name,
        address creator,
        address signer
    );

    event ProjectUpdated(
        string projectId,
        string name,
        address signer
    );

    event ProjectStatusChanged(
        string projectId,
        bool active
    );

    event Transaction(
        string projectId,
        address token,
        address account,
        uint256 amount,
        uint256 fee,
        string serialNo,
        TxType txType
    );

    event Paused(address account);
    event Unpaused(address account);

    // Functions
    function createProject(
        string calldata projectId,
        string calldata name,
        address signer
    ) external;

    function updateProject(
        string calldata projectId,
        string calldata name,
        address signer
    ) external;

    function setProjectStatus(
        string calldata projectId,
        bool active
    ) external;

    function pay(
        PaymentRequest calldata request,
        bytes calldata signature
    ) external payable;

    function payWithSign(
        PaymentRequest calldata request,
        bytes calldata signature
    ) external payable;

    function withdraw(
        WithdrawalRequest calldata request,
        bytes calldata signature
    ) external payable;

    function adminWithdraw(
        string calldata projectId,
        address token,
        uint256 amount,
        address recipient
    ) external;

    function depositToWithdrawalPool(
        string calldata projectId,
        address token,
        uint256 amount
    ) external payable;

    function withdrawFromWithdrawalPool(
        string calldata projectId,
        address token,
        uint256 amount,
        address recipient
    ) external;

    function pause() external;

    function unpause() external;

    function getProject(string calldata projectId) external view returns (Project memory);

    function getProjectBalance(
        string calldata projectId,
        address token
    ) external view returns (ProjectBalance memory);

    function isPaymentSerialNoUsed(
        string calldata projectId,
        string calldata serialNo
    ) external view returns (bool);

    function isWithdrawalSerialNoUsed(
        string calldata projectId,
        string calldata serialNo
    ) external view returns (bool);

    function getCreatorProjects(address creator) external view returns (string[] memory);

    function getCreatorProjectsPaginated(
        address creator,
        uint256 offset,
        uint256 limit
    ) external view returns (string[] memory projectIds, uint256 total);

    function getCreatorProjectCount(address creator) external view returns (uint256);
}

contracts/v1/tvm/libraries/TronSafeERC20.content:
// SPDX-License-Identifier: MIT
pragma solidity ^0.8.24;

import "@openzeppelin/contracts/token/ERC20/IERC20.sol";

/**
 * @title TronSafeERC20
 * @dev Wrappers around ERC20 operations that handle TRON's non-standard behavior.
 *
 * TRON USDT Issue:
 * - Standard ERC20 transfer/transferFrom should return true on success
 * - TRON USDT returns false (0x00..00) even on successful transfers
 * - OpenZeppelin's SafeERC20 reverts if return value is false
 *
 * Solution:
 * - For TRON tokens, we rely on the transfer event and balance changes
 * - instead of checking the return value
 */
library TronSafeERC20 {
    error TronSafeERC20FailedOperation(address token);
    error TronSafeERC20IncorrectAmount(address token, uint256 expected, uint256 received);

    /**
     * @dev Transfer tokens from one address to another.
     * On TRON, we verify success by checking balance changes rather than return value.
     * @param token The ERC20 token
     * @param from Source address
     * @param to Destination address
     * @param amount Amount to transfer
     */
    function safeTransferFrom(
        IERC20 token,
        address from,
        address to,
        uint256 amount
    ) internal {
        uint256 balanceBefore = token.balanceOf(to);

        // Call transferFrom - don't check return value for TRON compatibility
        (bool success, ) = address(token).call(
            abi.encodeCall(IERC20.transferFrom, (from, to, amount))
        );

        if (!success) {
            revert TronSafeERC20FailedOperation(address(token));
        }

        // Verify the transfer by checking balance change
        uint256 balanceAfter = token.balanceOf(to);
        uint256 received = balanceAfter - balanceBefore;

        // Allow for fee-on-transfer tokens, but ensure we received something
        if (received == 0 && amount > 0) {
            revert TronSafeERC20FailedOperation(address(token));
        }
    }

    /**
     * @dev Transfer tokens from one address to another with exact amount verification.
     * Reverts if received amount doesn't match expected amount (no fee-on-transfer support).
     * @param token The ERC20 token
     * @param from Source address
     * @param to Destination address
     * @param amount Amount to transfer (must match exactly)
     */
    function safeTransferFromExact(
        IERC20 token,
        address from,
        address to,
        uint256 amount
    ) internal {
        uint256 balanceBefore = token.balanceOf(to);

        // Call transferFrom - don't check return value for TRON compatibility
        (bool success, ) = address(token).call(
            abi.encodeCall(IERC20.transferFrom, (from, to, amount))
        );

        if (!success) {
            revert TronSafeERC20FailedOperation(address(token));
        }

        // Verify exact amount was received
        uint256 balanceAfter = token.balanceOf(to);
        uint256 received = balanceAfter - balanceBefore;

        if (received != amount) {
            revert TronSafeERC20IncorrectAmount(address(token), amount, received);
        }
    }

    /**
     * @dev Transfer tokens to a specified address.
     * On TRON, we verify success by checking balance changes rather than return value.
     * @param token The ERC20 token
     * @param to Destination address
     * @param amount Amount to transfer
     */
    function safeTransfer(
        IERC20 token,
        address to,
        uint256 amount
    ) internal {
        uint256 balanceBefore = token.balanceOf(to);

        // Call transfer - don't check return value for TRON compatibility
        (bool success, ) = address(token).call(
            abi.encodeCall(IERC20.transfer, (to, amount))
        );

        if (!success) {
            revert TronSafeERC20FailedOperation(address(token));
        }

        // Verify the transfer by checking balance change
        uint256 balanceAfter = token.balanceOf(to);
        uint256 received = balanceAfter - balanceBefore;

        // Allow for fee-on-transfer tokens, but ensure we received something
        if (received == 0 && amount > 0) {
            revert TronSafeERC20FailedOperation(address(token));
        }
    }

    /**
     * @dev Approve the specified address to spend the specified amount of tokens.
     * TRON USDT approve also returns false on success, so we don't check return value.
     * @param token The ERC20 token
     * @param spender Address to approve
     * @param amount Amount to approve
     */
    function safeApprove(
        IERC20 token,
        address spender,
        uint256 amount
    ) internal {
        (bool success, ) = address(token).call(
            abi.encodeCall(IERC20.approve, (spender, amount))
        );

        if (!success) {
            revert TronSafeERC20FailedOperation(address(token));
        }
    }

    /**
     * @dev Increase the approved amount of tokens.
     * @param token The ERC20 token
     * @param spender Address to increase approval for
     * @param value Amount to increase by
     */
    function safeIncreaseAllowance(
        IERC20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 oldAllowance = token.allowance(address(this), spender);
        safeApprove(token, spender, oldAllowance + value);
    }

    /**
     * @dev Decrease the approved amount of tokens.
     * @param token The ERC20 token
     * @param spender Address to decrease approval for
     * @param value Amount to decrease by
     */
    function safeDecreaseAllowance(
        IERC20 token,
        address spender,
        uint256 value
    ) internal {
        uint256 oldAllowance = token.allowance(address(this), spender);
        require(oldAllowance >= value, "TronSafeERC20: decreased allowance below zero");
        safeApprove(token, spender, oldAllowance - value);
    }

    /**
     * @dev Set approval to zero first, then set to new value.
     * Required for some tokens (like USDT) that require allowance to be 0 before setting a new value.
     * @param token The ERC20 token
     * @param spender Address to approve
     * @param amount Amount to approve
     */
    function forceApprove(
        IERC20 token,
        address spender,
        uint256 amount
    ) internal {
        // Try to approve directly first
        (bool success, ) = address(token).call(
            abi.encodeCall(IERC20.approve, (spender, amount))
        );

        // If failed or if we should reset first
        if (!success) {
            // Reset to 0 first
            (bool resetSuccess, ) = address(token).call(
                abi.encodeCall(IERC20.approve, (spender, 0))
            );
            if (!resetSuccess) {
                revert TronSafeERC20FailedOperation(address(token));
            }

            // Then set to new value
            if (amount > 0) {
                (bool setSuccess, ) = address(token).call(
                    abi.encodeCall(IERC20.approve, (spender, amount))
                );
                if (!setSuccess) {
                    revert TronSafeERC20FailedOperation(address(token));
                }
            }
        }
    }
}

